    DECLARE dia_semana INT;
    SET dia_semana = DAYOFWEEK(NEW.fecha);

    IF dia_semana = 1 THEN
        SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'Error: Taller cerrado los domingos.';
    END IF;

    IF dia_semana BETWEEN 2 AND 6 THEN
        IF NEW.hora < '08:00:00' OR NEW.hora > '18:00:00' THEN
            SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'Error: Fuera de horario (Lun-Vie 08-18hs).';
        END IF;
    END IF;

    IF dia_semana = 7 THEN
        IF NEW.hora < '08:00:00' OR NEW.hora > '13:00:00' THEN
